const App = () => {
   const purple = "pink";
   const [bg, setBg] = useState(purple);
   const [name, setName] = useState("click me");

   const bgChange = () => {
      // console.log("clicked");
      let newBg = "grey";
      setBg(newBg);
      setName("yeahh!!! ðŸ˜Š");
   }

   const bgBack = () => {
      let newBg = "red";
      setBg(newBg);
      setName("party!!! ðŸ¥³");

   }
   return (
      <>
         <div style={{ backgroundColor: bg }}>
            {/* <button onClick={bgChange} onDoubleClick={bgBack}> {name} </button> */}
            {/* <button > {name} </button> */}
            {/* <button onMouseDown={bgChange} onMouseUp={bgBack}> {name} </button> */}
            <button onMouseEnter={bgChange} onMouseLeave={bgBack}> {name} </button>
         </div> </>
   );
};


export default App;
