import React, { useState } from 'react';



const App = () => {
   const purple = "pink";
   const [bg , setBg] = useState(purple);
   const [name, setName] = useState('click me') ;

   const bgChange = () => {
      // console.log("clicked");
     let newBg = "grey";
     setBg(newBg);
   }
   return (
      <>
        <div style = {{backgroundColor: bg}}>
         <button onClick={bgChange}> {name} </button>
         </div> </>
   );
};


export default App;
