import React, { useState } from 'react';



const App = () => {
   const purple = "pink";
   const [bg , setBg] = useState(purple);
   const [name, setName] = useState("click me") ;

   const bgChange = () => {
      // console.log("clicked");
     let newBg = "grey";
     setBg(newBg);
     setName("yeahh!!! ðŸ˜Š");
   }

   const bgBack = () => {
      setBg(red);
      setName("party!!! ðŸ¥³");

   }
   return (
      <>
        <div style = {{backgroundColor: bg}}>
         <button onClick={bgChange}> {name} </button>
         <button onDoubleClick={bgBack}> {name} </button>

         </div> </>
   );
};


export default App;
